  module `IP_MODULE_NAME(efx_fifo_wrapper)(
			  //Output
			  almost_full_o,
			  prog_full_o,
			  full_o,
			  overflow_o,
			  wr_ack_o,
			  datacount_o,
			  wr_datacount_o,
			  empty_o,
			  almost_empty_o,
			  prog_empty_o,
			  underflow_o,
			  rd_valid_o,
